import React, { Component } from 'react'
import _ from 'lodash'
import moment from 'moment'
import currentDate from './../../../utilts/currentTime'

class TableBody extends Component {
  renderCell = (stopInfo, column) => {
    if (column.path === 'reaminTime') {
      const departureDate = new Date(
        _.get(stopInfo, 'scheduledDeparture') * 1000,
      )
        .toISOString()
        .substr(11, 8)
      var today = new Date()
      var currentDate =
        today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds()
      console.log(currentDate, departureDate)
      var startTime = moment(currentDate, 'HH:mm:ss')
      var endTime = moment(departureDate, 'HH:mm:ss')
      console.log('hrs: ', moment(endTime).diff(startTime, 'hours'))
      console.log(
        'dif in Mins: ',
        moment(endTime).diff(moment(startTime), 'seconds'),
      )

      //  diffmins=UpdateTimeSheet(departureDate)
      return moment(endTime).diff(moment(startTime), 'seconds') / 60
    }
    if (column.path === 'scheduledDeparture') {
      return new Date(_.get(stopInfo, column.path) * 1000)
        .toISOString()
        .substr(11, 8)
    }
    if (column.path === 'platformCode') {
      return _.get(stopInfo.stop, column.path)
    }
    return _.get(stopInfo, column.path)
  }

  createKey = (index, column) => {
    return index + (column.path || column.key)
  }

  render() {
    const { data, columns } = this.props

    return (
      <tbody>
        {data.map((stopInfo, index) => (
          <tr key={index}>
            {columns.map((column) => (
              <td key={this.createKey(index, column)}>
                {this.renderCell(stopInfo, column)}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    )
  }
}

export default TableBody
