import { FETCHING_DATA, FETCHING_FAILED, FETCHING_SUCCESS,GET_CURRENT_TIME } from '../../constants';

const initialState = {
	loading: false,
	data: {},
	error: '',
	columns : [
		{ path: 'scheduledDeparture', label: 'Time' },
		{ path: 'code', label: 'Code/Stop-id' },
		{ path: 'headsign', label: 'Destination' },
		{ path: 'platformCode', label: 'Platform' },
	  ],
	 time: new Date().toLocaleDateString('en-US', {
		weekday: 'short',
		year: 'numeric',
		month: 'short',
		day: 'numeric',
	  })
};

const stopsReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCHING_DATA:
			return { loading: true, data: {}, error: '',columns:state.columns, time:state.time};
		case FETCHING_FAILED:
			return { loading: false, data: {}, error: action.payload,columns:"" };
		case FETCHING_SUCCESS:
			return { loading: false, data: action.payload, error: '',columns:state.columns,time:state.time };
		default:
			return state;
	}
};

export default stopsReducer;
