import React, { useEffect, useState } from 'react'
import PropTypes from 'prop-types'

import { connect } from 'react-redux'
import { fetchingData, fetchingFailed, fetchingSuccess } from '../../actions'
import Table from './tableComponent/table'
/** =====================================================================
 *  Feel free to change anything you wish as well as using
 *  Classes instead of Functional Components.
 *
 *  More info on README.md file
 *  Doubts? <vinicius@choicely.com>
 *  ======================================================================= */
const url = 'https://api.digitransit.fi/routing/v1/routers/hsl/index/graphql'
const query = `{
  stop(id: "HSL:1020453") {
   name
   stoptimesWithoutPatterns(numberOfDepartures: 10) {
	stop {
		gtfsId
		name
		code
		platformCode
	}
	    serviceDay  
	    scheduledArrival
	    scheduledDeparture
	    headsign
        trip {
          route {
            shortName
            agency {
              name
            }
          }
          wheelchairAccessible
          bikesAllowed
        }        
      } 
  }  

}`

const options = {
  method: 'POST',
  headers: {
    'content-type': 'application/json',
  },
  body: JSON.stringify({
    query,
  }),
}
const Stops = (props) => {
  const { stops, fetchData, fetchFailed, fetchSuccess } = props
  const { data, loading, error } = stops
  const [curentTime, setCurrentTime] = useState()
  console.log(data)
  const columns = [
    { path: 'scheduledDeparture', label: 'Time' },
    // { path: 'reaminTime', label: 'Due' },
    { path: 'code', label: 'Code/Stop-id' },
    { path: 'headsign', label: 'Destination' },
    { path: 'platformCode', label: 'Platform' },
  ]
  function refreshDate() {
    setTimeout('getServiceDate();', 1000)
  }
  function getServiceDate() {
    // setTimeout(getServiceDate(date), 2000)
    const time = new Date().toLocaleDateString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
    })
    setCurrentTime(time)
    refreshDate()
  }
  useEffect(() => {
    fetchData()
    fetch(url, options)
      .then((res) => res.json())
      .then((data) => fetchSuccess(data))
      .catch((e) => fetchFailed(e.message))
  }, [fetchData, fetchFailed, fetchSuccess])
  // See the hints? :)
  getServiceDate()
  return (
    <div>
      {/* <h2>
        <span role="img" aria-label="lucky">
          üëçüçÄü§û
        </span>
        Good Luck!
        <span role="img" aria-label="lucky">
          ü§ûüçÄüëç
        </span>
      </h2> */}
      {error && <span>{error}</span>}
      {loading && <span>Loading ...</span>}
      {data && data.stoptimesWithoutPatterns && (
        <div>
          <h3 id="h3"> {data.name}</h3>
          <h4 id="h4"> {curentTime}</h4>
          <Table columns={columns} data={data.stoptimesWithoutPatterns} />
        </div>
      )}
    </div>
  )
}

Stops.protoTypes = {
  data: PropTypes.object,
  fetchData: PropTypes.func,
  fetchFailed: PropTypes.func,
  fetchSuccess: PropTypes.func,
}

function mapStateToProps(state) {
  const { stops } = state
  return {
    stops,
  }
}

function mapDispatchToProps(dispatch) {
  return {
    fetchData: () => {
      dispatch(fetchingData())
    },
    fetchFailed: (error) => {
      dispatch(fetchingFailed(error))
    },
    fetchSuccess: (data) => {
      dispatch(fetchingSuccess(data))
    },
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Stops)
