import { FETCHING_DATA, FETCHING_FAILED, FETCHING_SUCCESS,GET_CURRENT_TIME } from '../../constants';

const initialState = {
	loading: false,
	data: {},
	error: '',
	columns : [
		// { path: 'scheduledDeparture', label: 'Time' },
		{ refId:'time', path: 'realtimeDeparture', label: 'Time' },
		{ refId:'delay',path: ['scheduledDeparture','realtimeDeparture'], label: 'Delayed' },
		{ refId:'dueIn', path: 'realtimeDeparture', label: 'Due in' },
		{ refId:'desc',path: 'headsign', label: 'Destination' },
	  ],

};

const stopsReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCHING_DATA:
			return { loading: true, data: {}, error: '',columns:state.columns};
		case FETCHING_FAILED:
			return { loading: false, data: {}, error: action.payload,columns:"" };
		case FETCHING_SUCCESS:
			return { loading: false, data: action.payload, error: '',columns:state.columns };
		case UPDATE_DATE:
			return Object.assign({}, state, {
				data: state.data.map(item => {
					return item === action.payload ? action.payload : item;
				}) // replace matched item and returns the array 
			 }); 
			default:
			return state;
	}
};

export default stopsReducer;
