import React, { Component } from 'react'
import _ from 'lodash'

class TableBody extends Component {
  renderCell = (stopInfo, column) => {
    if (column.path === 'serviceDay') {
      var date = new Date(_.get(stopInfo, column.path) * 1000)
      // Hours part from the timestamp
      var hours = date.getHours()
      // Minutes part from the timestamp
      var minutes = '0' + date.getMinutes()
      // Seconds part from the timestamp
      var seconds = '0' + date.getSeconds()
      // Will display time in 10:30:23 format
      var formattedTime =
        hours + ':' + minutes.substr(-2) + ':' + seconds.substr(-2)
      return formattedTime

      console.log(formattedTime)
      return _.get(stopInfo, column.path)
    }
    if (column.path === 'scheduledArrival') {
      const date = new Date(_.get(stopInfo, column.path))
      var hours = date.getHours() < 10 ? '0' + date.getHours() : date.getHours()
      var minutes =
        date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()
      var formattedTime = hours + ':' + minutes
      return formattedTime
    }
    if (column.path === 'track') {
      return _.get(stopInfo.trick, column.path)
    }

    return _.get(stopInfo, column.path)
  }

  createKey = (index, column) => {
    return index + (column.path || column.key)
  }

  render() {
    const { data, columns } = this.props

    return (
      <tbody>
        {data.map((stopInfo, index) => (
          <tr key={index}>
            {columns.map((column) => (
              <td key={this.createKey(index, column)}>
                {this.renderCell(stopInfo, column)}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    )
  }
}

export default TableBody
