import React, { Component } from 'react'
import _ from 'lodash'

class TableBody extends Component {
  renderCell = (stopInfo, column) => {
    if (column.path === 'scheduledDeparture') {
      const unixTime = _.get(stopInfo, column.path)
      var options = {
        weekday: 'short',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
      }
      const date = new Date(unixTime * 1000)
      return date.toLocaleDateString('en-US', options)
    }
    if (column.path === 'scheduledArrival') {
      return new Date(_.get(stopInfo, column.path) * 1000)
        .toISOString()
        .substr(11, 8)
    }
    if (column.path === 'platformCode') {
      return _.get(stopInfo.stop, column.path)
    }

    return _.get(stopInfo, column.path)
  }

  createKey = (index, column) => {
    return index + (column.path || column.key)
  }

  render() {
    const { data, columns } = this.props

    return (
      <tbody>
        {data.map((stopInfo, index) => (
          <tr key={index}>
            {columns.map((column) => (
              <td key={this.createKey(index, column)}>
                {this.renderCell(stopInfo, column)}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    )
  }
}

export default TableBody
